from __future__ import absolute_import, division, unicode_literals

from xml.sax.xmlreader import AttributesNSImpl

from ..constants import adjust_foreign_attributes, unadjust_foreign_attributes

prefix_mapping = {}
for prefix, local_name, namespace in adjust_foreign_attributes.values():
    if prefix is not None:
        prefix_mapping[prefix] = namespace


def to_sax(walker, handler):
    """Call SAX-like content handler based on treewalker walker

    :arg walker: the treewalker to use to walk the tree to convert it

    :arg handler: SAX handler to use

    """
    handler.start_document()
    for prefix, namespace in prefix_mapping.items():
        handler.start_prefix_mapping(prefix, namespace)

    for token in walker:
        type = token["type"]
        if type == "Doctype":
            continue
        elif type in ("StartTag", "EmptyTag"):
            attrs = AttributesNSImpl(token["data"],
                                     unadjust_foreign_attributes)
            handler.start_element_ns((token["namespace"], token["name"]),
                                   token["name"],
                                   attrs)
            if type == "EmptyTag":
                handler.end_element_ns((token["namespace"], token["name"]),
                                     token["name"])
        elif type == "EndTag":
            handler.end_element_ns((token["namespace"], token["name"]),
                                 token["name"])
        elif type in ("Characters", "SpaceCharacters"):
            handler.characters(token["data"])
        elif type == "Comment":
            pass
        else:
            assert False, "Unknown token type"

    for prefix, namespace in prefix_mapping.items():
        handler.end_prefix_mapping(prefix)
    handler.end_document()
