# this version is Python 2.x-3.x cross-compatible
'decorator to trim function calls to match the arity of the target'
def _trim_arity(func, maxargs=2):
    if func in single_arg_builtins:
        return lambda s,l,t: func(t)
    limit = [0]
    found_arity = [False]
    
    # traceback return data structure changed in Py3.5 - normalize back to plain tuples
    if system_version[:2] >= (3,5):
        def extract_stack(limit=0):
            # special handling for Python 3.5.0 - extra deep call stack by 1
            offset = -3 if system_version == (3,5,0) else -2
            frame_summary = traceback.extract_stack(limit=-offset+limit-1)[offset]
            return [frame_summary[:2]]
        def extract_tb(tb, limit=0):
            frames = traceback.extract_tb(tb, limit=limit)
            frame_summary = frames[-1]
            return [frame_summary[:2]]
    else:
        extract_stack = traceback.extract_stack
        extract_tb = traceback.extract_tb
    
    # synthesize what would be returned by traceback.extract_stack at the call to 
    # user's parse action 'func', so that we don't incur call penalty at parse time
    
    LINE_DIFF = 6
    # IF ANY CODE CHANGES, EVEN JUST COMMENTS OR BLANK LINES, BETWEEN THE NEXT LINE AND 
    # THE CALL TO FUNC INSIDE WRAPPER, LINE_DIFF MUST BE MODIFIED!!!!
    this_line = extract_stack(limit=2)[-1]
    pa_call_line_synth = (this_line[0], this_line[1]+LINE_DIFF)

    def wrapper(*args):
        while 1:
            try:
                ret = func(*args[limit[0]:])
                found_arity[0] = True
                return ret
            except TypeError:
                # re-raise TypeErrors if they did not come from our arity testing
                if found_arity[0]:
                    raise
                else:
                    try:
                        tb = sys.exc_info()[-1]
                        if not extract_tb(tb, limit=2)[-1][:2] == pa_call_line_synth:
                            raise
                    finally:
                        del tb

                if limit[0] <= maxargs:
                    limit[0] += 1
                    continue
                raise

    # copy func name to wrapper for sensible debug output
    func_name = "<parse action>"
    try:
        func_name = getattr(func, '__name__', 
                            getattr(func, '__class__').__name__)
    except Exception:
        func_name = str(func)
    wrapper.__name__ = func_name

    return wrapper

class ParserElement(object):
    """Abstract base level parser element class."""
    DEFAULT_WHITE_CHARS = " \n\t\r"
    verbose_stacktrace = False

    @staticmethod
    def set_default_whitespace_chars( chars ):
        r"""
        Overrides the default whitespace chars

        Example::
            # default whitespace chars are space, <TAB> and newline
            OneOrMore(Word(alphas)).parse_string("abc def\nghi jkl")  # -> ['abc', 'def', 'ghi', 'jkl']
            
            # change to just treat newline as significant
            ParserElement.set_default_whitespace_chars(" \t")
            OneOrMore(Word(alphas)).parse_string("abc def\nghi jkl")  # -> ['abc', 'def']
        """
        ParserElement.DEFAULT_WHITE_CHARS = chars

    @staticmethod
    def inline_literals_using(cls):
        """
        Set class to be used for inclusion of string literals into a parser.
        
        Example::
            # default literal class used is Literal
            integer = Word(nums)
            date_str = integer("year") + '/' + integer("month") + '/' + integer("day")           

            date_str.parse_string("1999/12/31")  # -> ['1999', '/', '12', '/', '31']


            # change to Suppress
            ParserElement.inline_literals_using(Suppress)
            date_str = integer("year") + '/' + integer("month") + '/' + integer("day")           

            date_str.parse_string("1999/12/31")  # -> ['1999', '12', '31']
        """
        ParserElement._literal_string_class = cls

    def __init__( self, savelist=False ):
        self.parse_action = list()
        self.fail_action = None
        #~ self.name = "<unknown>"  # don't define self.name, let subclasses try/except upcall
        self.str_repr = None
        self.results_name = None
        self.save_as_list = savelist
        self.skip_whitespace = True
        self.white_chars = ParserElement.DEFAULT_WHITE_CHARS
        self.copy_default_white_chars = True
        self.may_return_empty = False # used when checking for left-recursion
        self.keep_tabs = False
        self.ignore_exprs = list()
        self.debug = False
        self.streamlined = False
        self.may_index_error = True # used to optimize exception handling for subclasses that don't advance parse index
        self.errmsg = ""
        self.modal_results = True # used to mark results names as modal (report only last) or cumulative (list all)
        self.debug_actions = ( None, None, None ) #custom debug actions
        self.re = None
        self.call_preparse = True # used to avoid redundant calls to pre_parse
        self.call_during_try = False

    def copy( self ):
        """
        Make a copy of this C{ParserElement}.  Useful for defining different parse actions
        for the same parsing pattern, using copies of the original parse element.
        
        Example::
            integer = Word(nums).set_parse_action(lambda toks: int(toks[0]))
            integer_k = integer.copy().add_parse_action(lambda toks: toks[0]*1024) + Suppress("K")
            integer_m = integer.copy().add_parse_action(lambda toks: toks[0]*1024*1024) + Suppress("M")
            
            print(OneOrMore(integer_k | integer_m | integer).parse_string("5K 100 640K 256M"))
        prints::
            [5120, 100, 655360, 268435456]
        Equivalent form of C{expr.copy()} is just C{expr()}::
            integer_m = integer().add_parse_action(lambda toks: toks[0]*1024*1024) + Suppress("M")
        """
        cpy = copy.copy( self )
        cpy.parse_action = self.parse_action[:]
        cpy.ignore_exprs = self.ignore_exprs[:]
        if self.copy_default_white_chars:
            cpy.white_chars = ParserElement.DEFAULT_WHITE_CHARS
        return cpy

    def set_name( self, name ):
        """
        Define name for this expression, makes debugging and exception messages clearer.
        
        Example::
            Word(nums).parse_string("ABC")  # -> Exception: Expected W:(0123...) (at char 0), (line:1, col:1)
            Word(nums).set_name("integer").parse_string("ABC")  # -> Exception: Expected integer (at char 0), (line:1, col:1)
        """
        self.name = name
        self.errmsg = "Expected " + self.name
        if hasattr(self,"exception"):
            self.exception.msg = self.errmsg
        return self

    def set_results_name( self, name, list_all_matches=False ):
        """
        Define name for referencing matching tokens as a nested attribute
        of the returned parse results.
        NOTE: this returns a *copy* of the original C{ParserElement} object;
        this is so that the client can define a basic element, such as an
        integer, and reference it in multiple places with different names.

        You can also set results names using the abbreviated syntax,
        C{expr("name")} in place of C{expr.set_results_name("name")} - 
        see L{I{__call__}<__call__>}.

        Example::
            date_str = (integer.set_results_name("year") + '/' 
                        + integer.set_results_name("month") + '/' 
                        + integer.set_results_name("day"))

            # equivalent form:
            date_str = integer("year") + '/' + integer("month") + '/' + integer("day")
        """
        newself = self.copy()
        if name.endswith("*"):
            name = name[:-1]
            list_all_matches=True
        newself.results_name = name
        newself.modal_results = not list_all_matches
        return newself

    def set_break(self,break_flag = True):
        """Method to invoke the Python pdb debugger when this element is
           about to be parsed. Set C{break_flag} to True to enable, False to
           disable.
        """
        if break_flag:
            _parse_method = self._parse
            def breaker(instring, loc, do_actions=True, call_pre_parse=True):
                import pdb
                pdb.set_trace()
                return _parse_method( instring, loc, do_actions, call_pre_parse )
            breaker._original_parse_method = _parse_method
            self._parse = breaker
        else:
            if hasattr(self._parse,"_original_parse_method"):
                self._parse = self._parse._original_parse_method
        return self

    def set_parse_action( self, *fns, **kwargs ):
        """
        Define one or more actions to perform when successfully matching parse element definition.
        Parse action fn is a callable method with 0-3 arguments, called as C{fn(s,loc,toks)},
        C{fn(loc,toks)}, C{fn(toks)}, or just C{fn()}, where:
         - s   = the original string being parsed (see note below)
         - loc = the location of the matching substring
         - toks = a list of the matched tokens, packaged as a C{L{ParseResults}} object
        If the functions in fns modify the tokens, they can return them as the return
        value from fn, and the modified list of tokens will replace the original.
        Otherwise, fn does not need to return any value.

        Optional keyword arguments:
         - call_during_try = (default=C{False}) indicate if parse action should be run during lookaheads and alternate testing

        Note: the default parsing behavior is to expand tabs in the input string
        before starting the parsing process.  See L{I{parse_string}<parse_string>} for more information
        on parsing strings containing C{<TAB>}s, and suggested methods to maintain a
        consistent view of the parsed string, the parse location, and line and column
        positions within the parsed string.
        
        Example::
            integer = Word(nums)
            date_str = integer + '/' + integer + '/' + integer

            date_str.parse_string("1999/12/31")  # -> ['1999', '/', '12', '/', '31']

            # use parse action to convert to ints at parse time
            integer = Word(nums).set_parse_action(lambda toks: int(toks[0]))
            date_str = integer + '/' + integer + '/' + integer

            # note that integer fields are now ints, not strings
            date_str.parse_string("1999/12/31")  # -> [1999, '/', 12, '/', 31]
        """
        self.parse_action = list(map(_trim_arity, list(fns)))
        self.call_during_try = kwargs.get("call_during_try", False)
        return self

    def add_parse_action( self, *fns, **kwargs ):
        """
        Add one or more parse actions to expression's list of parse actions. See L{I{set_parse_action}<set_parse_action>}.
        
        See examples in L{I{copy}<copy>}.
        """
        self.parse_action += list(map(_trim_arity, list(fns)))
        self.call_during_try = self.call_during_try or kwargs.get("call_during_try", False)
        return self

    def add_condition(self, *fns, **kwargs):
        """Add a boolean predicate function to expression's list of parse actions. See 
        L{I{set_parse_action}<set_parse_action>} for function call signatures. Unlike C{set_parse_action}, 
        functions passed to C{add_condition} need to return boolean success/fail of the condition.

        Optional keyword arguments:
         - message = define a custom message to be used in the raised exception
         - fatal   = if True, will raise ParseFatalException to stop parsing immediately; otherwise will raise ParseException
         
        Example::
            integer = Word(nums).set_parse_action(lambda toks: int(toks[0]))
            year_int = integer.copy()
            year_int.add_condition(lambda toks: toks[0] >= 2000, message="Only support years 2000 and later")
            date_str = year_int + '/' + integer + '/' + integer

            result = date_str.parse_string("1999/12/31")  # -> Exception: Only support years 2000 and later (at char 0), (line:1, col:1)
        """
        msg = kwargs.get("message", "failed user-defined condition")
        exc_type = ParseFatalException if kwargs.get("fatal", False) else ParseException
        for fn in fns:
            def pa(s,l,t):
                if not bool(_trim_arity(fn)(s,l,t)):
                    raise exc_type(s,l,msg)
            self.parse_action.append(pa)
        self.call_during_try = self.call_during_try or kwargs.get("call_during_try", False)
        return self

    def set_fail_action( self, fn ):
        """Define action to perform if parsing fails at this expression.
           Fail acton fn is a callable function that takes the arguments
           C{fn(s,loc,expr,err)} where:
            - s = string being parsed
            - loc = location where expression match was attempted and failed
            - expr = the parse expression that failed
            - err = the exception thrown
           The function returns no value.  It may throw C{L{ParseFatalException}}
           if it is desired to stop parsing immediately."""
        self.fail_action = fn
        return self

    def _skip_ignorables( self, instring, loc ):
        exprs_found = True
        while exprs_found:
            exprs_found = False
            for e in self.ignore_exprs:
                try:
                    while 1:
                        loc,dummy = e._parse( instring, loc )
                        exprs_found = True
                except ParseException:
                    pass
        return loc

    def pre_parse( self, instring, loc ):
        if self.ignore_exprs:
            loc = self._skip_ignorables( instring, loc )

        if self.skip_whitespace:
            wt = self.white_chars
            instrlen = len(instring)
            while loc < instrlen and instring[loc] in wt:
                loc += 1

        return loc

    def parse_impl( self, instring, loc, do_actions=True ):
        return loc, []

    def post_parse( self, instring, loc, tokenlist ):
        return tokenlist

    #~ @profile
    def _parse_no_cache( self, instring, loc, do_actions=True, call_pre_parse=True ):
        debugging = ( self.debug ) #and do_actions )

        if debugging or self.fail_action:
            #~ print ("Match",self,"at loc",loc,"(%d,%d)" % ( lineno(loc,instring), col(loc,instring) ))
            if (self.debug_actions[0] ):
                self.debug_actions[0]( instring, loc, self )
            if call_pre_parse and self.call_preparse:
                preloc = self.pre_parse( instring, loc )
            else:
                preloc = loc
            tokens_start = preloc
            try:
                try:
                    loc,tokens = self.parse_impl( instring, preloc, do_actions )
                except IndexError:
                    raise ParseException( instring, len(instring), self.errmsg, self )
            except ParseBaseException as err:
                #~ print ("Exception raised:", err)
                if self.debug_actions[2]:
                    self.debug_actions[2]( instring, tokens_start, self, err )
                if self.fail_action:
                    self.fail_action( instring, tokens_start, self, err )
                raise
        else:
            if call_pre_parse and self.call_preparse:
                preloc = self.pre_parse( instring, loc )
            else:
                preloc = loc
            tokens_start = preloc
            if self.may_index_error or preloc >= len(instring):
                try:
                    loc,tokens = self.parse_impl( instring, preloc, do_actions )
                except IndexError:
                    raise ParseException( instring, len(instring), self.errmsg, self )
            else:
                loc,tokens = self.parse_impl( instring, preloc, do_actions )

        tokens = self.post_parse( instring, loc, tokens )

        ret_tokens = ParseResults( tokens, self.results_name, as_list=self.save_as_list, modal=self.modal_results )
        if self.parse_action and (do_actions or self.call_during_try):
            if debugging:
                try:
                    for fn in self.parse_action:
                        tokens = fn( instring, tokens_start, ret_tokens )
                        if tokens is not None:
                            ret_tokens = ParseResults( tokens,
                                                      self.results_name,
                                                      as_list=self.save_as_list and isinstance(tokens,(ParseResults,list)),
                                                      modal=self.modal_results )
                except ParseBaseException as err:
                    #~ print "Exception raised in user parse action:", err
                    if (self.debug_actions[2] ):
                        self.debug_actions[2]( instring, tokens_start, self, err )
                    raise
            else:
                for fn in self.parse_action:
                    tokens = fn( instring, tokens_start, ret_tokens )
                    if tokens is not None:
                        ret_tokens = ParseResults( tokens,
                                                  self.results_name,
                                                  as_list=self.save_as_list and isinstance(tokens,(ParseResults,list)),
                                                  modal=self.modal_results )
        if debugging:
            #~ print ("Matched",self,"->",ret_tokens.as_list())
            if (self.debug_actions[1] ):
                self.debug_actions[1]( instring, tokens_start, loc, self, ret_tokens )

        return loc, ret_tokens

    def try_parse( self, instring, loc ):
        try:
            return self._parse( instring, loc, do_actions=False )[0]
        except ParseFatalException:
            raise ParseException( instring, loc, self.errmsg, self)
    
    def can_parse_next(self, instring, loc):
        try:
            self.try_parse(instring, loc)
        except (ParseException, IndexError):
            return False
        else:
            return True

    class _UnboundedCache(object):
        def __init__(self):
            cache = {}
            self.not_in_cache = not_in_cache = object()

            def get(self, key):
                return cache.get(key, not_in_cache)

            def set(self, key, value):
                cache[key] = value

            def clear(self):
                cache.clear()
                
            def cache_len(self):
                return len(cache)

            self.get = types.MethodType(get, self)
            self.set = types.MethodType(set, self)
            self.clear = types.MethodType(clear, self)
            self.__len__ = types.MethodType(cache_len, self)

    if _OrderedDict is not None:
        class _FifoCache(object):
            def __init__(self, size):
                self.not_in_cache = not_in_cache = object()

                cache = _OrderedDict()

                def get(self, key):
                    return cache.get(key, not_in_cache)

                def set(self, key, value):
                    cache[key] = value
                    while len(cache) > size:
                        try:
                            cache.popitem(False)
                        except KeyError:
                            pass

                def clear(self):
                    cache.clear()

                def cache_len(self):
                    return len(cache)

                self.get = types.MethodType(get, self)
                self.set = types.MethodType(set, self)
                self.clear = types.MethodType(clear, self)
                self.__len__ = types.MethodType(cache_len, self)

    else:
        class _FifoCache(object):
            def __init__(self, size):
                self.not_in_cache = not_in_cache = object()

                cache = {}
                key_fifo = collections.deque([], size)

                def get(self, key):
                    return cache.get(key, not_in_cache)

                def set(self, key, value):
                    cache[key] = value
                    while len(key_fifo) > size:
                        cache.pop(key_fifo.popleft(), None)
                    key_fifo.append(key)

                def clear(self):
                    cache.clear()
                    key_fifo.clear()

                def cache_len(self):
                    return len(cache)

                self.get = types.MethodType(get, self)
                self.set = types.MethodType(set, self)
                self.clear = types.MethodType(clear, self)
                self.__len__ = types.MethodType(cache_len, self)

    # argument cache for optimizing repeated calls when backtracking through recursive expressions
    packrat_cache = {} # this is set later by enabled_packrat(); this is here so that reset_cache() doesn't fail
    packrat_cache_lock = RLock()
    packrat_cache_stats = [0, 0]

    # this method gets repeatedly called during backtracking with the same arguments -
    # we can cache these arguments and save ourselves the trouble of re-parsing the contained expression
    def _parse_cache( self, instring, loc, do_actions=True, call_pre_parse=True ):
        HIT, MISS = 0, 1
        lookup = (self, instring, loc, call_pre_parse, do_actions)
        with ParserElement.packrat_cache_lock:
            cache = ParserElement.packrat_cache
            value = cache.get(lookup)
            if value is cache.not_in_cache:
                ParserElement.packrat_cache_stats[MISS] += 1
                try:
                    value = self._parse_no_cache(instring, loc, do_actions, call_pre_parse)
                except ParseBaseException as pe:
                    # cache a copy of the exception, without the traceback
                    cache.set(lookup, pe.__class__(*pe.args))
                    raise
                else:
                    cache.set(lookup, (value[0], value[1].copy()))
                    return value
            else:
                ParserElement.packrat_cache_stats[HIT] += 1
                if isinstance(value, Exception):
                    raise value
                return (value[0], value[1].copy())

    _parse = _parse_no_cache

    @staticmethod
    def reset_cache():
        ParserElement.packrat_cache.clear()
        ParserElement.packrat_cache_stats[:] = [0] * len(ParserElement.packrat_cache_stats)

    _packrat_enabled = False
    @staticmethod
    def enable_packrat(cache_size_limit=128):
        """Enables "packrat" parsing, which adds memoizing to the parsing logic.
           Repeated parse attempts at the same string location (which happens
           often in many complex grammars) can immediately return a cached value,
           instead of re-executing parsing/validating code.  Memoizing is done of
           both valid results and parsing exceptions.
           
           Parameters:
            - cache_size_limit - (default=C{128}) - if an integer value is provided
              will limit the size of the packrat cache; if None is passed, then
              the cache size will be unbounded; if 0 is passed, the cache will
              be effectively disabled.
            
           This speedup may break existing programs that use parse actions that
           have side-effects.  For this reason, packrat parsing is disabled when
           you first import pyparsing.  To activate the packrat feature, your
           program must call the class method C{ParserElement.enable_packrat()}.  If
           your program uses C{psyco} to "compile as you go", you must call
           C{enable_packrat} before calling C{psyco.full()}.  If you do not do this,
           Python will crash.  For best results, call C{enable_packrat()} immediately
           after importing pyparsing.
           
           Example::
               import pyparsing
               pyparsing.ParserElement.enable_packrat()
        """
        if not ParserElement._packrat_enabled:
            ParserElement._packrat_enabled = True
            if cache_size_limit is None:
                ParserElement.packrat_cache = ParserElement._UnboundedCache()
            else:
                ParserElement.packrat_cache = ParserElement._FifoCache(cache_size_limit)
            ParserElement._parse = ParserElement._parse_cache

    def parse_string( self, instring, parse_all=False ):
        """
        Execute the parse expression with the given string.
        This is the main interface to the client code, once the complete
        expression has been built.

        If you want the grammar to require that the entire input string be
        successfully parsed, then set C{parse_all} to True (equivalent to ending
        the grammar with C{L{StringEnd()}}).

        Note: C{parse_string} implicitly calls C{expandtabs()} on the input string,
        in order to report proper column numbers in parse actions.
        If the input string contains tabs and
        the grammar uses parse actions that use the C{loc} argument to index into the
        string being parsed, you can ensure you have a consistent view of the input
        string by:
         - calling C{parse_with_tabs} on your grammar before calling C{parse_string}
           (see L{I{parse_with_tabs}<parse_with_tabs>})
         - define your parse action using the full C{(s,loc,toks)} signature, and
           reference the input string using the parse action's C{s} argument
         - explictly expand the tabs in your input string before calling
           C{parse_string}
        
        Example::
            Word('a').parse_string('aaaaabaaa')  # -> ['aaaaa']
            Word('a').parse_string('aaaaabaaa', parse_all=True)  # -> Exception: Expected end of text
        """
        ParserElement.reset_cache()
        if not self.streamlined:
            self.streamline()
            #~ self.save_as_list = True
        for e in self.ignore_exprs:
            e.streamline()
        if not self.keep_tabs:
            instring = instring.expandtabs()
        try:
            loc, tokens = self._parse( instring, 0 )
            if parse_all:
                loc = self.pre_parse( instring, loc )
                se = Empty() + StringEnd()
                se._parse( instring, loc )
        except ParseBaseException as exc:
            if ParserElement.verbose_stacktrace:
                raise
            else:
                # catch and re-raise exception from here, clears out pyparsing internal stack trace
                raise exc
        else:
            return tokens

    def scan_string( self, instring, max_matches=_MAX_INT, overlap=False ):
        """
        Scan the input string for expression matches.  Each match will return the
        matching tokens, start location, and end location.  May be called with optional
        C{max_matches} argument, to clip scanning after 'n' matches are found.  If
        C{overlap} is specified, then overlapping matches will be reported.

        Note that the start and end locations are reported relative to the string
        being parsed.  See L{I{parse_string}<parse_string>} for more information on parsing
        strings with embedded tabs.

        Example::
            source = "sldjf123lsdjjkf345sldkjf879lkjsfd987"
            print(source)
            for tokens,start,end in Word(alphas).scan_string(source):
                print(' '*start + '^'*(end-start))
                print(' '*start + tokens[0])
        
        prints::
        
            sldjf123lsdjjkf345sldkjf879lkjsfd987
            ^^^^^
            sldjf
                    ^^^^^^^
                    lsdjjkf
                              ^^^^^^
                              sldkjf
                                       ^^^^^^
                                       lkjsfd
        """
        if not self.streamlined:
            self.streamline()
        for e in self.ignore_exprs:
            e.streamline()

        if not self.keep_tabs:
            instring = _ustr(instring).expandtabs()
        instrlen = len(instring)
        loc = 0
        preparse_fn = self.pre_parse
        parse_fn = self._parse
        ParserElement.reset_cache()
        matches = 0
        try:
            while loc <= instrlen and matches < max_matches:
                try:
                    preloc = preparse_fn( instring, loc )
                    next_loc,tokens = parse_fn( instring, preloc, call_pre_parse=False )
                except ParseException:
                    loc = preloc+1
                else:
                    if next_loc > loc:
                        matches += 1
                        yield tokens, preloc, next_loc
                        if overlap:
                            nextloc = preparse_fn( instring, loc )
                            if nextloc > loc:
                                loc = next_loc
                            else:
                                loc += 1
                        else:
                            loc = next_loc
                    else:
                        loc = preloc+1
        except ParseBaseException as exc:
            if ParserElement.verbose_stacktrace:
                raise
            else:
                # catch and re-raise exception from here, clears out pyparsing internal stack trace
                raise exc

    def transform_string( self, instring ):
        """
        Extension to C{L{scan_string}}, to modify matching text with modified tokens that may
        be returned from a parse action.  To use C{transform_string}, define a grammar and
        attach a parse action to it that modifies the returned token list.
        Invoking C{transform_string()} on a target string will then scan for matches,
        and replace the matched text patterns according to the logic in the parse
        action.  C{transform_string()} returns the resulting transformed string.
        
        Example::
            wd = Word(alphas)
            wd.set_parse_action(lambda toks: toks[0].title())
            
            print(wd.transform_string("now is the winter of our discontent made glorious summer by this sun of york."))
        Prints::
            Now Is The Winter Of Our Discontent Made Glorious Summer By This Sun Of York.
        """
        out = []
        last_e = 0
        # force preservation of <TAB>s, to minimize unwanted transformation of string, and to
        # keep string locs straight between transform_string and scan_string
        self.keep_tabs = True
        try:
            for t,s,e in self.scan_string( instring ):
                out.append( instring[last_e:s] )
                if t:
                    if isinstance(t,ParseResults):
                        out += t.as_list()
                    elif isinstance(t,list):
                        out += t
                    else:
                        out.append(t)
                last_e = e
            out.append(instring[last_e:])
            out = [o for o in out if o]
            return "".join(map(_ustr,_flatten(out)))
        except ParseBaseException as exc:
            if ParserElement.verbose_stacktrace:
                raise
            else:
                # catch and re-raise exception from here, clears out pyparsing internal stack trace
                raise exc

    def search_string( self, instring, max_matches=_MAX_INT ):
        """
        Another extension to C{L{scan_string}}, simplifying the access to the tokens found
        to match the given parse expression.  May be called with optional
        C{max_matches} argument, to clip searching after 'n' matches are found.
        
        Example::
            # a capitalized word starts with an uppercase letter, followed by zero or more lowercase letters
            cap_word = Word(alphas.upper(), alphas.lower())
            
            print(cap_word.search_string("More than Iron, more than Lead, more than Gold I need Electricity"))

            # the sum() builtin can be used to merge results into a single ParseResults object
            print(sum(cap_word.search_string("More than Iron, more than Lead, more than Gold I need Electricity")))
        prints::
            [['More'], ['Iron'], ['Lead'], ['Gold'], ['I'], ['Electricity']]
            ['More', 'Iron', 'Lead', 'Gold', 'I', 'Electricity']
        """
        try:
            return ParseResults([ t for t,s,e in self.scan_string( instring, max_matches ) ])
        except ParseBaseException as exc:
            if ParserElement.verbose_stacktrace:
                raise
            else:
                # catch and re-raise exception from here, clears out pyparsing internal stack trace
                raise exc

    def split(self, instring, maxsplit=_MAX_INT, include_separators=False):
        """
        Generator method to split a string using the given expression as a separator.
        May be called with optional C{maxsplit} argument, to limit the number of splits;
        and the optional C{include_separators} argument (default=C{False}), if the separating
        matching text should be included in the split results.
        
        Example::        
            punc = one_of(list(".,;:/-!?"))
            print(list(punc.split("This, this?, this sentence, is badly punctuated!")))
        prints::
            ['This', ' this', '', ' this sentence', ' is badly punctuated', '']
        """
        splits = 0
        last = 0
        for t,s,e in self.scan_string(instring, max_matches=maxsplit):
            yield instring[last:s]
            if include_separators:
                yield t[0]
            last = e
        yield instring[last:]

    def __add__(self, other ):
        """
        Implementation of + operator - returns C{L{And}}. Adding strings to a ParserElement
        converts them to L{Literal}s by default.
        
        Example::
            greet = Word(alphas) + "," + Word(alphas) + "!"
            hello = "Hello, World!"
            print (hello, "->", greet.parse_string(hello))
        Prints::
            Hello, World! -> ['Hello', ',', 'World', '!']
        """
        if isinstance( other, basestring ):
            other = ParserElement._literal_string_class( other )
        if not isinstance( other, ParserElement ):
            warnings.warn("Cannot combine element of type %s with ParserElement" % type(other),
                    SyntaxWarning, stacklevel=2)
            return None
        return And( [ self, other ] )

    def __radd__(self, other ):
        """
        Implementation of + operator when left operand is not a C{L{ParserElement}}
        """
        if isinstance( other, basestring ):
            other = ParserElement._literal_string_class( other )
        if not isinstance( other, ParserElement ):
            warnings.warn("Cannot combine element of type %s with ParserElement" % type(other),
                    SyntaxWarning, stacklevel=2)
            return None
        return other + self

    def __sub__(self, other):
        """
        Implementation of - operator, returns C{L{And}} with error stop
        """
        if isinstance( other, basestring ):
            other = ParserElement._literal_string_class( other )
        if not isinstance( other, ParserElement ):
            warnings.warn("Cannot combine element of type %s with ParserElement" % type(other),
                    SyntaxWarning, stacklevel=2)
            return None
        return self + And._ErrorStop() + other

    def __rsub__(self, other ):
        """
        Implementation of - operator when left operand is not a C{L{ParserElement}}
        """
        if isinstance( other, basestring ):
            other = ParserElement._literal_string_class( other )
        if not isinstance( other, ParserElement ):
            warnings.warn("Cannot combine element of type %s with ParserElement" % type(other),
                    SyntaxWarning, stacklevel=2)
            return None
        return other - self

    def __mul__(self,other):
        """
        Implementation of * operator, allows use of C{expr * 3} in place of
        C{expr + expr + expr}.  Expressions may also me multiplied by a 2-integer
        tuple, similar to C{{min,max}} multipliers in regular expressions.  Tuples
        may also include C{None} as in:
         - C{expr*(n,None)} or C{expr*(n,)} is equivalent
              to C{expr*n + L{ZeroOrMore}(expr)}
              (read as "at least n instances of C{expr}")
         - C{expr*(None,n)} is equivalent to C{expr*(0,n)}
              (read as "0 to n instances of C{expr}")
         - C{expr*(None,None)} is equivalent to C{L{ZeroOrMore}(expr)}
         - C{expr*(1,None)} is equivalent to C{L{OneOrMore}(expr)}

        Note that C{expr*(None,n)} does not raise an exception if
        more than n exprs exist in the input stream; that is,
        C{expr*(None,n)} does not enforce a maximum number of expr
        occurrences.  If this behavior is desired, then write
        C{expr*(None,n) + ~expr}
        """
        if isinstance(other,int):
            min_elements, opt_elements = other,0
        elif isinstance(other,tuple):
            other = (other + (None, None))[:2]
            if other[0] is None:
                other = (0, other[1])
            if isinstance(other[0],int) and other[1] is None:
                if other[0] == 0:
                    return ZeroOrMore(self)
                if other[0] == 1:
                    return OneOrMore(self)
                else:
                    return self*other[0] + ZeroOrMore(self)
            elif isinstance(other[0],int) and isinstance(other[1],int):
                min_elements, opt_elements = other
                opt_elements -= min_elements
            else:
                raise TypeError("cannot multiply 'ParserElement' and ('%s','%s') objects", type(other[0]),type(other[1]))
        else:
            raise TypeError("cannot multiply 'ParserElement' and '%s' objects", type(other))

        if min_elements < 0:
            raise ValueError("cannot multiply ParserElement by negative value")
        if opt_elements < 0:
            raise ValueError("second tuple value must be greater or equal to first tuple value")
        if min_elements == opt_elements == 0:
            raise ValueError("cannot multiply ParserElement by 0 or (0,0)")

        if (opt_elements):
            def make_optional_list(n):
                if n>1:
                    return Optional(self + make_optional_list(n-1))
                else:
                    return Optional(self)
            if min_elements:
                if min_elements == 1:
                    ret = self + make_optional_list(opt_elements)
                else:
                    ret = And([self]*min_elements) + make_optional_list(opt_elements)
            else:
                ret = make_optional_list(opt_elements)
        else:
            if min_elements == 1:
                ret = self
            else:
                ret = And([self]*min_elements)
        return ret

    def __rmul__(self, other):
        return self.__mul__(other)

