def _make_tags(tag_str, xml):
    """Internal helper to construct opening and closing tag expressions, given a tag name"""
    if isinstance(tag_str,basestring):
        resname = tag_str
        tag_str = Keyword(tag_str, caseless=not xml)
    else:
        resname = tag_str.name

    tag_attr_name = Word(alphas,alphanums+"_-:")
    if (xml):
        tag_attr_value = dbl_quoted_string.copy().set_parse_action( remove_quotes )
        open_tag = Suppress("<") + tag_str("tag") + \
                Dict(ZeroOrMore(Group( tag_attr_name + Suppress("=") + tag_attr_value ))) + \
                Optional("/",default=[False]).set_results_name("empty").set_parse_action(lambda s,l,t:t[0]=='/') + Suppress(">")
    else:
        printables_less_r_abrack = "".join(c for c in printables if c not in ">")
        tag_attr_value = quoted_string.copy().set_parse_action( remove_quotes ) | Word(printables_less_r_abrack)
        open_tag = Suppress("<") + tag_str("tag") + \
                Dict(ZeroOrMore(Group( tag_attr_name.set_parse_action(downcase_tokens) + \
                Optional( Suppress("=") + tag_attr_value ) ))) + \
                Optional("/",default=[False]).set_results_name("empty").set_parse_action(lambda s,l,t:t[0]=='/') + Suppress(">")
    close_tag = Combine(_L("</") + tag_str + ">")

    open_tag = open_tag.set_results_name("start"+"".join(resname.replace(":"," ").title().split())).set_name("<%s>" % resname)
    close_tag = close_tag.set_results_name("end"+"".join(resname.replace(":"," ").title().split())).set_name("</%s>" % resname)
    open_tag.tag = resname
    close_tag.tag = resname
    return open_tag, close_tag

def make_html_tags(tag_str):
    """
    Helper to construct opening and closing tag expressions for HTML, given a tag name. Matches
    tags in either upper or lower case, attributes with namespaces and with quoted or unquoted values.

    Example::
        text = '<td>More info at the <a href="http://pyparsing.wikispaces.com">pyparsing</a> wiki page</td>'
        # make_html_tags returns pyparsing expressions for the opening and closing tags as a 2-tuple
        a,a_end = make_html_tags("A")
        link_expr = a + SkipTo(a_end)("link_text") + a_end
        
        for link in link_expr.search_string(text):
            # attributes in the <A> tag (like "href" shown here) are also accessible as named results
            print(link.link_text, '->', link.href)
    prints::
        pyparsing -> http://pyparsing.wikispaces.com
    """
    return _make_tags( tag_str, False )

def make_xml_tags(tag_str):
    """
    Helper to construct opening and closing tag expressions for XML, given a tag name. Matches
    tags only in the given upper/lower case.

    Example: similar to L{make_html_tags}
    """
    return _make_tags( tag_str, True )

def with_attribute(*args,**attr_dict):
    """
    Helper to create a validating parse action to be used with start tags created
    with C{L{make_xml_tags}} or C{L{make_html_tags}}. Use C{with_attribute} to qualify a starting tag
    with a required attribute value, to avoid false matches on common tags such as
    C{<TD>} or C{<DIV>}.

    Call C{with_attribute} with a series of attribute names and values. Specify the list
    of filter attributes names and values as:
     - keyword arguments, as in C{(align="right")}, or
     - as an explicit dict with C{**} operator, when an attribute name is also a Python
          reserved word, as in C{**{"class":"Customer", "align":"right"}}
     - a list of name-value tuples, as in ( ("ns1:class", "Customer"), ("ns2:align","right") )
    For attribute names with a namespace prefix, you must use the second form.  Attribute
    names are matched insensitive to upper/lower case.
       
    If just testing for C{class} (with or without a namespace), use C{L{with_class}}.

    To verify that the attribute exists, but without specifying a value, pass
    C{with_attribute.ANY_VALUE} as the value.

    Example::
        html = '''
            <div>
            Some text
            <div type="grid">1 4 0 1 0</div>
            <div type="graph">1,3 2,3 1,1</div>
            <div>this has no type</div>
            </div>
                
        '''
        div,div_end = make_html_tags("div")

        # only match div tag having a type attribute with value "grid"
        div_grid = div().set_parse_action(with_attribute(type="grid"))
        grid_expr = div_grid + SkipTo(div | div_end)("body")
        for grid_header in grid_expr.search_string(html):
            print(grid_header.body)
        
        # construct a match with any div tag having a type attribute, regardless of the value
        div_any_type = div().set_parse_action(with_attribute(type=with_attribute.ANY_VALUE))
        div_expr = div_any_type + SkipTo(div | div_end)("body")
        for div_header in div_expr.search_string(html):
            print(div_header.body)
    prints::
        1 4 0 1 0

        1 4 0 1 0
        1,3 2,3 1,1
    """
    if args:
        attrs = args[:]
    else:
        attrs = attr_dict.items()
    attrs = [(k,v) for k,v in attrs]
    def pa(s,l,tokens):
        for attr_name,attr_value in attrs:
            if attr_name not in tokens:
                raise ParseException(s,l,"no matching attribute " + attr_name)
            if attr_value != with_attribute.ANY_VALUE and tokens[attr_name] != attr_value:
                raise ParseException(s,l,"attribute '%s' has value '%s', must be '%s'" %
                                            (attr_name, tokens[attr_name], attr_value))
    return pa
with_attribute.ANY_VALUE = object()

def with_class(classname, namespace=''):
    """
    Simplified version of C{L{with_attribute}} when matching on a div class - made
    difficult because C{class} is a reserved word in Python.

    Example::
        html = '''
            <div>
            Some text
            <div class="grid">1 4 0 1 0</div>
            <div class="graph">1,3 2,3 1,1</div>
            <div>this &lt;div&gt; has no class</div>
            </div>
                
        '''
        div,div_end = make_html_tags("div")
        div_grid = div().set_parse_action(with_class("grid"))
        
        grid_expr = div_grid + SkipTo(div | div_end)("body")
        for grid_header in grid_expr.search_string(html):
            print(grid_header.body)
        
        div_any_type = div().set_parse_action(with_class(with_attribute.ANY_VALUE))
        div_expr = div_any_type + SkipTo(div | div_end)("body")
        for div_header in div_expr.search_string(html):
            print(div_header.body)
    prints::
        1 4 0 1 0

        1 4 0 1 0
        1,3 2,3 1,1
    """
    classattr = "%s:class" % namespace if namespace else "class"
    return with_attribute(**{classattr : classname})        

op_assoc = _Constants()
op_assoc.LEFT = object()
op_assoc.RIGHT = object()

def infix_notation( base_expr, op_list, lpar=Suppress('('), rpar=Suppress(')') ):
    """
    Helper method for constructing grammars of expressions made up of
    operators working in a precedence hierarchy.  Operators may be unary or
    binary, left- or right-associative.  Parse actions can also be attached
    to operator expressions. The generated parser will also recognize the use 
    of parentheses to override operator precedences (see example below).
    
    Note: if you define a deep operator list, you may see performance issues
    when using infix_notation. See L{ParserElement.enable_packrat} for a
    mechanism to potentially improve your parser performance.

    Parameters:
     - base_expr - expression representing the most basic element for the nested
     - op_list - list of tuples, one for each operator precedence level in the
      expression grammar; each tuple is of the form
      (op_expr, num_terms, right_left_assoc, parse_action), where:
       - op_expr is the pyparsing expression for the operator;
          may also be a string, which will be converted to a Literal;
          if num_terms is 3, op_expr is a tuple of two expressions, for the
          two operators separating the 3 terms
       - num_terms is the number of terms for this operator (must
          be 1, 2, or 3)
       - right_left_assoc is the indicator whether the operator is
          right or left associative, using the pyparsing-defined
          constants C{op_assoc.RIGHT} and C{op_assoc.LEFT}.
       - parse_action is the parse action to be associated with
          expressions matching this operator expression (the
          parse action tuple member may be omitted); if the parse action
          is passed a tuple or list of functions, this is equivalent to
          calling C{set_parse_action(*fn)} (L{ParserElement.set_parse_action})
     - lpar - expression for matching left-parentheses (default=C{Suppress('(')})
     - rpar - expression for matching right-parentheses (default=C{Suppress(')')})

    Example::
        # simple example of four-function arithmetic with ints and variable names
        integer = pyparsing_common.signed_integer
        varname = pyparsing_common.identifier 
        
        arith_expr = infix_notation(integer | varname,
            [
            ('-', 1, op_assoc.RIGHT),
            (one_of('* /'), 2, op_assoc.LEFT),
            (one_of('+ -'), 2, op_assoc.LEFT),
            ])
        
        arith_expr.run_tests('''
            5+3*6
            (5+3)*6
            -2--11
            ''', full_dump=False)
    prints::
        5+3*6
        [[5, '+', [3, '*', 6]]]

        (5+3)*6
        [[[5, '+', 3], '*', 6]]

        -2--11
        [[['-', 2], '-', ['-', 11]]]
    """
    ret = Forward()
    last_expr = base_expr | ( lpar + ret + rpar )
    for i,oper_def in enumerate(op_list):
        op_expr,arity,right_left_assoc,pa = (oper_def + (None,))[:4]
        term_name = "%s term" % op_expr if arity < 3 else "%s%s term" % op_expr
        if arity == 3:
            if op_expr is None or len(op_expr) != 2:
                raise ValueError("if numterms=3, op_expr must be a tuple or list of two expressions")
            op_expr1, op_expr2 = op_expr
        this_expr = Forward().set_name(term_name)
        if right_left_assoc == op_assoc.LEFT:
            if arity == 1:
                match_expr = FollowedBy(last_expr + op_expr) + Group( last_expr + OneOrMore( op_expr ) )
            elif arity == 2:
                if op_expr is not None:
                    match_expr = FollowedBy(last_expr + op_expr + last_expr) + Group( last_expr + OneOrMore( op_expr + last_expr ) )
                else:
                    match_expr = FollowedBy(last_expr+last_expr) + Group( last_expr + OneOrMore(last_expr) )
            elif arity == 3:
                match_expr = FollowedBy(last_expr + op_expr1 + last_expr + op_expr2 + last_expr) + \
                            Group( last_expr + op_expr1 + last_expr + op_expr2 + last_expr )
            else:
                raise ValueError("operator must be unary (1), binary (2), or ternary (3)")
        elif right_left_assoc == op_assoc.RIGHT:
            if arity == 1:
                # try to avoid LR with this extra test
                if not isinstance(op_expr, Optional):
                    op_expr = Optional(op_expr)
                match_expr = FollowedBy(op_expr.expr + this_expr) + Group( op_expr + this_expr )
            elif arity == 2:
                if op_expr is not None:
                    match_expr = FollowedBy(last_expr + op_expr + this_expr) + Group( last_expr + OneOrMore( op_expr + this_expr ) )
                else:
                    match_expr = FollowedBy(last_expr + this_expr) + Group( last_expr + OneOrMore( this_expr ) )
            elif arity == 3:
                match_expr = FollowedBy(last_expr + op_expr1 + this_expr + op_expr2 + this_expr) + \
                            Group( last_expr + op_expr1 + this_expr + op_expr2 + this_expr )
            else:
                raise ValueError("operator must be unary (1), binary (2), or ternary (3)")
        else:
            raise ValueError("operator must indicate right or left associativity")
        if pa:
            if isinstance(pa, (tuple, list)):
                match_expr.set_parse_action(*pa)
            else:
                match_expr.set_parse_action(pa)
        this_expr <<= ( match_expr.set_name(term_name) | last_expr )
        last_expr = this_expr
    ret <<= last_expr
    return ret

