def nested_expr(opener="(", closer=")", content=None, ignore_expr=quoted_string.copy()):
    """
    Helper method for defining nested lists enclosed in opening and closing
    delimiters ("(" and ")" are the default).

    Parameters:
     - opener - opening character for a nested list (default=C{"("}); can also be a pyparsing expression
     - closer - closing character for a nested list (default=C{")"}); can also be a pyparsing expression
     - content - expression for items within the nested lists (default=C{None})
     - ignore_expr - expression for ignoring opening and closing delimiters (default=C{quoted_string})

    If an expression is not provided for the content argument, the nested
    expression will capture all whitespace-delimited content between delimiters
    as a list of separate values.

    Use the C{ignore_expr} argument to define expressions that may contain
    opening or closing characters that should not be treated as opening
    or closing characters for nesting, such as quoted_string or a comment
    expression.  Specify multiple expressions using an C{L{Or}} or C{L{MatchFirst}}.
    The default is L{quoted_string}, but if no expressions are to be ignored,
    then pass C{None} for this argument.

    Example::
        data_type = one_of("void int short long char float double")
        decl_data_type = Combine(data_type + Optional(Word('*')))
        ident = Word(alphas+'_', alphanums+'_')
        number = pyparsing_common.number
        arg = Group(decl_data_type + ident)
        LPAR,RPAR = map(Suppress, "()")

        code_body = nested_expr('{', '}', ignore_expr=(quoted_string | c_style_comment))

        c_function = (decl_data_type("type") 
                      + ident("name")
                      + LPAR + Optional(delimited_list(arg), [])("args") + RPAR 
                      + code_body("body"))
        c_function.ignore(c_style_comment)
        
        source_code = '''
            int is_odd(int x) { 
                return (x%2); 
            }
                
            int dec_to_hex(char hchar) { 
                if (hchar >= '0' && hchar <= '9') { 
                    return (ord(hchar)-ord('0')); 
                } else { 
                    return (10+ord(hchar)-ord('A'));
                } 
            }
        '''
        for func in c_function.search_string(source_code):
            print("%(name)s (%(type)s) args: %(args)s" % func)

    prints::
        is_odd (int) args: [['int', 'x']]
        dec_to_hex (int) args: [['char', 'hchar']]
    """
    if opener == closer:
        raise ValueError("opening and closing strings cannot be the same")
    if content is None:
        if isinstance(opener,basestring) and isinstance(closer,basestring):
            if len(opener) == 1 and len(closer)==1:
                if ignore_expr is not None:
                    content = (Combine(OneOrMore(~ignore_expr +
                                    CharsNotIn(opener+closer+ParserElement.DEFAULT_WHITE_CHARS,exact=1))
                                ).set_parse_action(lambda t:t[0].strip()))
                else:
                    content = (empty.copy()+CharsNotIn(opener+closer+ParserElement.DEFAULT_WHITE_CHARS
                                ).set_parse_action(lambda t:t[0].strip()))
            else:
                if ignore_expr is not None:
                    content = (Combine(OneOrMore(~ignore_expr + 
                                    ~Literal(opener) + ~Literal(closer) +
                                    CharsNotIn(ParserElement.DEFAULT_WHITE_CHARS,exact=1))
                                ).set_parse_action(lambda t:t[0].strip()))
                else:
                    content = (Combine(OneOrMore(~Literal(opener) + ~Literal(closer) +
                                    CharsNotIn(ParserElement.DEFAULT_WHITE_CHARS,exact=1))
                                ).set_parse_action(lambda t:t[0].strip()))
        else:
            raise ValueError("opening and closing arguments must be strings if no content expression is given")
    ret = Forward()
    if ignore_expr is not None:
        ret <<= Group( Suppress(opener) + ZeroOrMore( ignore_expr | ret | content ) + Suppress(closer) )
    else:
        ret <<= Group( Suppress(opener) + ZeroOrMore( ret | content )  + Suppress(closer) )
    ret.set_name('nested %s%s expression' % (opener,closer))
    return ret

def indented_block(block_statement_expr, indent_stack, indent=True):
    """
    Helper method for defining space-delimited indentation blocks, such as
    those used to define block statements in Python source code.

    Parameters:
     - block_statement_expr - expression defining syntax of statement that
            is repeated within the indented block
     - indent_stack - list created by caller to manage indentation stack
            (multiple statement_with_indented_block expressions within a single grammar
            should share a common indent_stack)
     - indent - boolean indicating whether block must be indented beyond the
            the current level; set to False for block of left-most statements
            (default=C{True})

    A valid block must contain at least one C{block_statement}.

    Example::
        data = '''
        def A(z):
          A1
          B = 100
          G = A2
          A2
          A3
        B
        def BB(a,b,c):
          BB1
          def BBA():
            bba1
            bba2
            bba3
        C
        D
        def spam(x,y):
             def eggs(z):
                 pass
        '''


        indent_stack = [1]
        stmt = Forward()

        identifier = Word(alphas, alphanums)
        func_decl = ("def" + identifier + Group( "(" + Optional( delimited_list(identifier) ) + ")" ) + ":")
        func_body = indented_block(stmt, indent_stack)
        func_def = Group( func_decl + func_body )

        rvalue = Forward()
        func_call = Group(identifier + "(" + Optional(delimited_list(rvalue)) + ")")
        rvalue << (func_call | identifier | Word(nums))
        assignment = Group(identifier + "=" + rvalue)
        stmt << ( func_def | assignment | identifier )

        module_body = OneOrMore(stmt)

        parse_tree = module_body.parse_string(data)
        parse_tree.pprint()
    prints::
        [['def',
          'A',
          ['(', 'z', ')'],
          ':',
          [['A1'], [['B', '=', '100']], [['G', '=', 'A2']], ['A2'], ['A3']]],
         'B',
         ['def',
          'BB',
          ['(', 'a', 'b', 'c', ')'],
          ':',
          [['BB1'], [['def', 'BBA', ['(', ')'], ':', [['bba1'], ['bba2'], ['bba3']]]]]],
         'C',
         'D',
         ['def',
          'spam',
          ['(', 'x', 'y', ')'],
          ':',
          [[['def', 'eggs', ['(', 'z', ')'], ':', [['pass']]]]]]] 
    """
    def check_peer_indent(s,l,t):
        if l >= len(s): return
        cur_col = col(l,s)
        if cur_col != indent_stack[-1]:
            if cur_col > indent_stack[-1]:
                raise ParseFatalException(s,l,"illegal nesting")
            raise ParseException(s,l,"not a peer entry")

    def check_sub_indent(s,l,t):
        cur_col = col(l,s)
        if cur_col > indent_stack[-1]:
            indent_stack.append( cur_col )
        else:
            raise ParseException(s,l,"not a subentry")

    def check_unindent(s,l,t):
        if l >= len(s): return
        cur_col = col(l,s)
        if not(indent_stack and cur_col < indent_stack[-1] and cur_col <= indent_stack[-2]):
            raise ParseException(s,l,"not an unindent")
        indent_stack.pop()

    NL = OneOrMore(LineEnd().set_whitespace_chars("\t ").suppress())
    INDENT = (Empty() + Empty().set_parse_action(check_sub_indent)).set_name('INDENT')
    PEER   = Empty().set_parse_action(check_peer_indent).set_name('')
    UNDENT = Empty().set_parse_action(check_unindent).set_name('UNINDENT')
    if indent:
        sm_expr = Group( Optional(NL) +
            #~ FollowedBy(block_statement_expr) +
            INDENT + (OneOrMore( PEER + Group(block_statement_expr) + Optional(NL) )) + UNDENT)
    else:
        sm_expr = Group( Optional(NL) +
            (OneOrMore( PEER + Group(block_statement_expr) + Optional(NL) )) )
    block_statement_expr.ignore(_bslash + LineEnd())
    return sm_expr.set_name('indented block')

